import React, { Component } from 'react';
import axios from 'axios';
import './FullPost.css';

class FullPost extends Component {
    state = {
        laodedPost : null
    }
    componentDidMount (){

    }
    componentDidUpdate(){
        this.loadData
    }
    loadData(){
        if(this.props.match.params.id){
            if(!this.state.laodedPost || (this.state.laodedPost && this.state.laodedPost.id !== this.props.id)){
                axios.get('/posts/' + this.props.match.params.id)
            .then(response => {
                this.setState({laodedPost:response.data})
            })
            }
        }
    }
    deletePostHandler = () => {
        axios.delete('/posts/' + this.props.id)
            .then(response => {
                console.log(response);
            })
    }
    render () {
        let post = <p style={{textAlign:'center'}}>Please select a Post!</p>;
        if(this.props.id){
             post = <p style={{textAlign:'center'}}>Loading.....</p>;  
        }
        if(this.state.laodedPost){
            post = (
                <div className="FullPost">
                    <h1>{this.state.laodedPost.title}</h1>
                    <p>{this.state.laodedPost.body}</p>
                    <div className="Edit">
                        <button className="Delete" onClick={this.deletePostHandler}>Delete</button>
                    </div>
                </div>
            );
        }

        return post;
    }
}

export default FullPost;
