import React, { Component } from 'react';
import classes from './Blog.module.css';
import Posts from './Posts/Posts';
import NewPost from './NewPost/NewPost';
import FullPost from './FullPost/FullPost';
import {Route, NavLink} from 'react-router-dom';

//import axios from 'axios';
// import axios from '../../axios';

class Blog extends Component {
    render () {

        return (    
            <div className={classes.Blog}>
                <header>
                    <nav>
                        <ul>
                            <li>
                                <NavLink to="/" exact
                             activeClassName = 'my-ative'
                             activeStyle = {{
                                 color : '#fa923f',
                                 textDecoration : 'underline'
                             }}
                                >Home</NavLink>
                            </li>
                            <li><NavLink to={{
                                            pathname : '/new-post',
                                            hash :'#submit',
                                            search : '?quick-submit=true'
                                        }}> New Post
                                </NavLink>
                            </li>
                        </ul>
                    </nav>
                </header>
                {/* <Route path='/' exact render={() => <h1>Home1</h1>} />
                <Route path='/' render={() => <h1>Home1</h1>} /> */}
                with 'switch', the first route that matches the given pth
                <Route path='/' exact component={Posts} />
                <Route path='/new-post' component={NewPost} />
                <Route path='/:id' component={FullPost} />
            </div>
        );
    }
}

export default Blog;


/* if you want to add a relative path then use -- 
        "pathname : this.props.match.url + '/new-post' "
        this will set url like if you are on 'example.com/post' then new url will be --
        'example.com/post/new-post'  
        Otherwise it will only be 'example.com/new-post' 
        IF you want to highlight the current link when it's active then
          use 'NavLink' instead of 'Link' it allows some extra features like adding 
          inline style .... by default 'activeClassName' is 'active' 
          but you can give your choice's name 
 */
